import React, { Component } from 'react';

class Register extends Component {    
    constructor( props) {
        super( props );
        this.state = {
            name: '',
            email: '',
            password: ''
        }
    }

    onNameChange = (event) => {
        this.setState({ name: event.target.value })
    }

    onEmailChange = (event) => {
        this.setState({ email: event.target.value })
    }
    
    onPasswordChange = (event) => {
        this.setState({ password: event.target.value })
    }

    onSubmitRegister = () => {
        fetch('https://protected-dawn-20665.herokuapp.com/register', {
            method: 'post',
            headers: { 'Content-Type': 'application/json' } ,
            body: JSON.stringify({
                name: this.state.name,
                email: this.state.email,
                password: this.state.password
            })
        })
        .then(response => response.json())
        .then(user => {
            if(user.id){
                this.props.loadUser(user);
                this.props.onRouteChange('home');
            }
        })
    }

    render() {
        return (
            <article className="br3 ba b--black-10 mv3 w-100 w-80-m w-25-l mw6 shadow-5 center">
                <main className="pa4 black-80">
                <div className="measure">
                <fieldset id="sign_up" className="ba b--transparent ph0 mh0">
                    <legend className="f2 fw6 ph0 mh0">Register</legend>
                    <div className="mt3">
                        <label className="db fw6 lh-copy f6" htmlFor="name">Name</label>
                        <input
                            onChange = { this.onNameChange } 
                            className="pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100" 
                            type="text" 
                            name="name"  
                            id="name"     
                        />
                    </div>
                    <div className="mt3">
                        <label className="db fw6 lh-copy f6" htmlFor="email-address">Email</label>
                        <input
                            onChange = { this.onEmailChange } 
                            className="pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100" 
                            type="email" 
                            name="email-address"  
                            id="email-address"                                 
                        />
                    </div>
                    <div className="mv3">
                        <label className="db fw6 lh-copy f6" htmlFor="password">Password</label>
                        <input 
                            onChange = { this.onPasswordChange }
                            className="b pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100" 
                            type="password" 
                            name="password"  
                            id="password"                                 
                        />
                    </div>
                </fieldset>
                <div className="">
                    <input 
                        className="b ph3 pv2 input-reset ba b--washed-green bg-light-green grow pointer f6 dib" 
                        type="submit" 
                        value="Register"
                        onClick = { this.onSubmitRegister } />
                </div>
                </div>
            </main>
        </article>  
        );
    }    
}

export default Register;
